import React from "react";
import { Routes, Route, Outlet } from "react-router-dom";
import Main from "./pages/Main";
import NotFound from "./pages/NotFound";
import Header from "./components/Header";
import AsidePromotion from "./components/AsidePromotion";
import Login from "./pages/Login";

function Shell({ children }: { children: React.ReactNode }) {
  return (
    <div className="min-h-screen mx-20 bg-sky-bg">
      {/* 왼쪽 고정 사이드바 (fixed left-0 top-0 h-screen w-[360px]) */}
      <AsidePromotion />

      {/* 오른쪽 컨텐츠 래퍼: 사이드바 폭만큼 여백 확보 */}
      <div className="mx-auto w-full max-w-[1280px] px-4 xl:pl-[360px]">
        <main
          className="
            w-[420px]
            bg-white shadow-app xl:overflow-hidden
            min-h-screen
            flex flex-col
            mx-auto
          "
        >
          {children}
        </main>
      </div>
    </div>
  );
}





/** 헤더가 포함된 레이아웃 */
function LayoutWithHeader() {
  return (
    <Shell>
      <Header />
      <section className="flex-1 bg-[#f9fbff]">
        <Outlet />
      </section>
    </Shell>
  );
}

/** 헤더가 없는 레이아웃 */
function LayoutWithoutHeader() {
  return (
    <Shell>
      <section className="flex-1 bg-[#f9fbff]">
        <Outlet />
      </section>
    </Shell>
  );
}

export default function App() {
  return (
    <Routes>
      {/* 헤더가 있는 페이지들 */}
      <Route element={<LayoutWithHeader />}>
        <Route index element={<Main />} />
        {/* 필요시 더 추가 */}
      </Route>

      {/* 헤더가 없는 페이지들 */}
      <Route element={<LayoutWithoutHeader />}>
        <Route path="login" element={<Login />} />
      </Route>

      {/* 그 외 */}
      <Route element={<LayoutWithHeader />}>
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}
