import React, { useEffect } from "react";

type ItemKey = "products" | "stylebook" | "contact" | "notice";

type Props = {
  open: boolean;
  onClose: () => void;
  onItemClick?: (key: ItemKey) => void;
};

export default function LoginRequiredModal({ open, onClose, onItemClick }: Props) {
  // ESC 닫기 + 뒤 스크롤 잠금
  useEffect(() => {
    const onKey = (e: KeyboardEvent) => e.key === "Escape" && onClose();
    if (open) {
      document.addEventListener("keydown", onKey);
      document.body.style.overflow = "hidden";
    }
    return () => {
      document.removeEventListener("keydown", onKey);
      document.body.style.overflow = "";
    };
  }, [open, onClose]);

  if (!open) return null;

  return (
    <div
      className="fixed inset-0 z-[1000]"
      role="dialog"
      aria-modal="true"
      aria-labelledby="login-required-title"
    >
      {/* 어두운 배경 */}
      <div className="absolute inset-0 bg-black/80" onClick={onClose} />

      {/* 중앙 패널 (모바일 폭, 양옆 여백) */}
      <div className="relative mx-auto h-full max-w-[480px] px-5">
        <div className="mt-10 bg-white shadow-xl">
          {/* 헤더 */}
          <div className="relative px-6 pt-7 pb-5">
            <h2 id="login-required-title" className="text-[28px] leading-tight font-semibold">
              <span className="font-extrabold underline underline-offset-4">로그인</span>
              이 필요합니다.
            </h2>

            {/* 닫기(X) */}
            <button
              aria-label="닫기"
              onClick={onClose}
              className="absolute right-3 top-3 h-10 w-10 grid place-items-center rounded-full hover:bg-black/5 active:scale-95 transition"
            >
              {/* SVG 아이콘 (이미지로 바꾸려면 <img src="/assets/close.png" … /> ) */}
              <svg width="24" height="24" viewBox="0 0 24 24">
                <path d="M6 6l12 12M18 6L6 18" stroke="currentColor" strokeWidth="2" />
              </svg>
            </button>
          </div>

          {/* 굵은 구분선 */}
          <div className="px-6">
            <hr className="border-black" />
          </div>

          {/* 메뉴 목록 */}
          <nav className="px-6 py-6">
            <ul className="space-y-10 text-[28px] leading-none">
              <li>
                <button
                  className="w-full text-left hover:opacity-80"
                  onClick={() => onItemClick?.("products")}
                >
                  상품조회
                </button>
              </li>
              <li>
                <button
                  className="w-full text-left hover:opacity-80"
                  onClick={() => onItemClick?.("stylebook")}
                >
                  스타일 북
                </button>
              </li>
              <li>
                <button
                  className="w-full text-left hover:opacity-80"
                  onClick={() => onItemClick?.("contact")}
                >
                  문의 채널
                </button>
              </li>
              <li>
                <button
                  className="w-full text-left hover:opacity-80"
                  onClick={() => onItemClick?.("notice")}
                >
                  공지 사항
                </button>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </div>
  );
}
