import React from "react";
import { Routes, Route, Outlet } from "react-router-dom";
import Main from "./pages/Main";
import NotFound from "./pages/NotFound";
import Header from "./components/Header";
import AsidePromotion from "./components/AsidePromotion";
import Login from "./pages/Login";

function Shell({ children }: { children: React.ReactNode }) {
  return (
    <div className="min-h-screen bg-sky-bg grid xl:grid-cols-[1fr_auto] place-items-center xl:items-stretch">
      {/* 좌측 프로모션 (넓을 때만 보임) */}
      <AsidePromotion />

      {/* 우측 앱 프레임 */}
      <main className="w-full w-[440px] mx-40 bg-white shadow-app xl:overflow-hidden min-h-screen flex flex-col mx-10">
        {children}
      </main>
    </div>
  );
}

/** 헤더가 포함된 레이아웃 */
function LayoutWithHeader() {
  return (
    <Shell>
      <Header />
      <section className="flex-1 bg-[#f9fbff]">
        <Outlet />
      </section>
    </Shell>
  );
}

/** 헤더가 없는 레이아웃 */
function LayoutWithoutHeader() {
  return (
    <Shell>
      <section className="flex-1 bg-[#f9fbff]">
        <Outlet />
      </section>
    </Shell>
  );
}

export default function App() {
  return (
    <Routes>
      {/* 헤더가 있는 페이지들 */}
      <Route element={<LayoutWithHeader />}>
        <Route index element={<Main />} />
        {/* 필요시 더 추가 */}
      </Route>

      {/* 헤더가 없는 페이지들 */}
      <Route element={<LayoutWithoutHeader />}>
        <Route path="login" element={<Login />} />
      </Route>

      {/* 그 외 */}
      <Route element={<LayoutWithHeader />}>
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}
