// src/App.tsx
import React from "react";
import { Routes, Route, Outlet } from "react-router-dom";
import Main from "./pages/Main";
import NotFound from "./pages/NotFound";
// import "./App.css";  // Tailwind만 쓰면 이건 지워도 됨

function Layout() {
  return (
    <div className="min-h-screen w-full bg-sky-bg flex justify-center">
      {/* 중앙 앱 프레임 */}
      <div className="flex flex-col w-full max-w-[min(90vw,420px)] bg-white shadow-app xl:rounded-app xl:overflow-hidden min-h-screen">
        {/* Header */}
        <header className="h-14 flex items-center justify-between px-4 border-b">
          <h1 className="text-lg font-bold">Lendy</h1>
          <button className="text-sm text-gray-600 hover:text-black">프로필</button>
        </header>

        {/* Page Area */}
        <main className="flex-1 p-4 bg-[#f9fbff]">
          <Outlet />
        </main>

        {/* Footer */}
        <footer className="h-12 flex items-center justify-center border-t text-sm text-gray-500">
          © 2025 Lendy
        </footer>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Routes>
      <Route element={<Layout />}>
        <Route index element={<Main />} />
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}
