import React from "react";
import { Routes, Route, Outlet } from "react-router-dom";
import Main from "./pages/Main";
import NotFound from "./pages/NotFound";

function Layout() {
  return (
    <div className="min-h-screen bg-sky-bg grid xl:grid-cols-[1fr_auto] place-items-center xl:items-stretch">
      {/* 좌측 프로모션(넓을 때만 보임) */}
      <aside className="hidden xl:flex flex-col gap-2 px-[3vw] py-16">
        <h2 className="text-3xl font-bold">먼저 써보고 결제는 나중에!</h2>
        <p className="text-slate-600 max-w-[520px] leading-relaxed">
          실물로 써보기 전에… 원하는 카피를 넣으세요. 가까운 매장에서 체험하고 마음에 들면 구매!
        </p>
        <button className="w-fit rounded-full bg-black text-white px-5 py-3">
          써보기 무료 예약
        </button>
        {/* 데코 원 */}
        <div className="relative w-64 h-64">
          <div className="absolute inset-0 rounded-full bg-blue-200/40 blur-2xl" />
          <div className="absolute right-0 top-0 w-24 h-24 rounded-full bg-indigo-300/70" />
          <div className="absolute left-6 bottom-6 w-14 h-14 rounded-full bg-sky-300/70" />
        </div>
      </aside>

      {/* 우측 앱 프레임 */}
      <main className="w-full w-[450px] px-10 bg-white shadow-app xl:rounded-app xl:overflow-hidden min-h-screen flex flex-col">
        {/* 공통 헤더 */}
        <Header />
        {/* 페이지 */}
        <section className="flex-1 bg-[#f9fbff]">
          <Outlet />
        </section>
      </main>
    </div>
  );
}

function Header() {
  return (
    <header className="h-14 bg-white border-b flex items-center justify-between px-3">
      <button aria-label="menu" className="p-2 -ml-1">
        {/* 햄버거 */}
        <span className="block w-5 h-0.5 bg-black mb-1"></span>
        <span className="block w-5 h-0.5 bg-black mb-1"></span>
        <span className="block w-5 h-0.5 bg-black"></span>
      </button>

      <div className="font-extrabold tracking-tight">ROUNZ</div>

      <div className="flex items-center gap-2">
        <button aria-label="search" className="p-2">🔍</button>
        <button aria-label="user" className="p-2">👤</button>
        <button aria-label="cart" className="relative p-2">
          🛒
          <span className="absolute -right-0.5 -top-0.5 bg-blue-600 text-white text-[10px] leading-4 rounded-full w-4 h-4 text-center">
            0
          </span>
        </button>
      </div>
    </header>
  );
}

export default function App() {
  return (
    <Routes>
      <Route element={<Layout />}>
        <Route index element={<Main />} />
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}
