import React from "react";

export default function AsidePromotion() {
  return (
    <aside className="hidden xl:flex flex-col gap-2 ml-40 px-[3vw] py-16">
      <h2 className="text-3xl font-bold">먼저 써보고 결제는 나중에!</h2>
      <p className="text-slate-600 max-w-[520px] leading-relaxed">
        실물로 써보기 전에… 원하는 카피를 넣으세요. 가까운 매장에서 체험하고 마음에 들면 구매!
      </p>
      <button className="w-fit rounded-full bg-black text-white px-5 py-3">
        옷 대여하기
      </button>

      {/* 데코 원 */}
      <div className="relative w-64 h-64">
        <div className="absolute inset-0 rounded-full bg-blue-200/40 blur-2xl" />
        <div className="absolute right-0 top-0 w-24 h-24 rounded-full bg-indigo-300/70" />
        <div className="absolute left-6 bottom-6 w-14 h-14 rounded-full bg-gray-300/70" />
      </div>
    </aside>
  );
}
